javascript:(function(){const r=Array.from(document.querySelectorAll("tr"));let t=0,e=0;for(const n of r){const c=Array.from(n.querySelectorAll(":scope > td, :scope > th"));if(c.length<=1)continue;t++;const s=new Set;for(const o of c){const i=(o.innerText||o.textContent||"").replace(/\u00A0/g," ").replace(/\s+/g," ").trim();if(s.has(i)){o.remove();e++}else s.add(i)}}console.log(`âœ… Done. Rows scanned: ${r.length}. Rows touched: ${t}. Cells removed: ${e}.`);})();
