javascript:(function(){const r=Array.from(document.querySelectorAll("tr"));let t=0,e=0;for(const n of r){const c=Array.from(n.querySelectorAll(":scope > td, :scope > th"));if(c.length<=1)continue;const seen=new Set();let removedInRow=0;for(const cell of c){const classes=Array.from(cell.classList).map(cls=>cls.trim()).filter(Boolean);if(classes.length===0)continue;const key=classes.sort().join(" ");if(seen.has(key)){cell.remove();removedInRow++;e++}else seen.add(key)}if(removedInRow>0)t++}console.log(`âœ… Done. Rows scanned: ${r.length}. Rows with removals: ${t}. Cells removed: ${e}.`);})();
