javascript:(function(){const e=t=>[...t.classList].some(t=>/^css-.*-diff-removed$/.test(t)),o=Array.from(document.querySelectorAll("td")).filter(e);if(0===o.length){alert("No <td> with class matching css-*-diff-removed found.");return}const r=new Map;for(const e of o){const o=e.closest("tr");o&&(r.has(o)||r.set(o,new Set),r.get(o).add(e))}let s=0,l=0;for(const[e,o]of r.entries()){let r=0;e.querySelectorAll(":scope > td, :scope > th").forEach(e=>{o.has(e)||(e.remove(),r++)}),r>0&&(s++,l+=r)}console.log(`âœ… Done. Rows changed: ${s}, cells removed: ${l}.`);})();
